/* 19 calculadora de intervalos 220223
nmr= numero
abj= abajo
arr= arriba
cnt= cantidad
c= contador
tabla de multiplicar en un intervalo para un numero(nmr)
*/
#include <iostream>
using namespace std;
int main(){
    int abj,arr,nmr,c,ab,r;
    c=ab;
    while(c<=arr){
        r=nmr*c;
        cout<<"*"<<nmr<<"="<<r<<endl;
        c+=1;
    return 0;
}
}